const counters = document.querySelectorAll(".value"), speed = 200, options = {threshold: .5},
    observer = new IntersectionObserver(((e, t) => {
        e.forEach((e => {
            e.isIntersecting && (startCounting(e.target), t.unobserve(e.target))
        }))
    }), options);

function startCounting(e) {
    const t = () => {
        const n = +e.getAttribute("akhi"), r = +e.innerText, o = n / 200;
        r < n ? (e.innerText = Math.ceil(r + o), setTimeout(t, 1)) : e.innerText = n
    };
    t()
}

counters.forEach((e => {
    observer.observe(e)
}));